package My_Learning.recusrion;

public class SuperPowerLeetCode372 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	}

	public int superPow(int a, int[] b) {
		if (a % 1337 == 0)
			return 0;
		int p = 0;
		for (int i : b)
			p = (p * 10 + i) % 1140;
		if (p == 0)
			p += 1440;
		return power(a, p, 1337);
	}

	public int power(int a, int n, int mod) {
		a %= mod;
		int ret = 1;
		while (n != 0) {
			if ((n & 1) != 0)
				ret = ret * a % mod;
			a = a * a % mod;
			n >>= 1;
		}
		return ret;
	}

}
